import React from "react";
import SideIcons from "./SideIcons";
import ThemeContext from '../ThemeContext';

function setColor() {
    var x = document.getElementsByClassName("");
    var typ = document.createAttribute("style");
    typ.value = { color: "var(--on-background)" };

    for (var i = 0; i < x.length; i++) {
        x[i].attributes.setNamedItem(typ);
    }

    if (window.location.pathname == "/") {
        x = document.getElementsByName("head");
        typ = document.createAttribute("style");
        typ.value = { color: "var(--secondary-color)" };
        x.attributes.setNamedItem(typ);
    } else {
        const path = window.location.pathname.slice(1);
        x = document.getElemenstByName(path);
        typ = document.createAttribute("style");
        typ.value = { color: "var(--secondary-color)" };
        x.attributes.setNamedItem(typ);
    }
}

function NavBar() {
    return <ThemeContext.Consumer>
        {(value) =>
            <div className={"navbar " + value.theme}>
                <SideIcons type="head" name="Home" onClick={setColor} />
                <div className="navbar-icons">
                    <ul className="navbar-list">
                        <SideIcons type="text" name="About" click={setColor} />
                        <SideIcons type="text" name="Skills" click={setColor} />
                        <SideIcons type="text" name="Experience" click={setColor} />
                        <SideIcons type="text" name="Projects" click={setColor} />
                        <SideIcons type="text" name="Contact" click={setColor} />
                    </ul>
                </div>
            </div>
        }
    </ThemeContext.Consumer>
}

export default NavBar;
